from .at_auth import get_access_token  # Correct importfrom payments.payment_module import MpesaPaymentdef initiate_payment(phone_number, amount):    try:        mpesa = MpesaPayment()  # Instantiate the MpesaPayment class        response = mpesa.initiate_payment(            phone_number=phone_number,  # Use the user-provided phone number            amount=amount,  # Use the validated amount            callback_url="https://62bd-2c0f-6300-d0b-c800-89ee-637-48ac-ec9e.ngrok-free.app/driver_hiring_system/payments/callback/"        )        print("Payment Initiated:", response)        return response    except Exception as e:        print("Payment Error:", str(e))        return {"error": str(e)}# Example Usage (for testing, use dynamic values)if __name__ == "__main__":    phone_number = input("Enter phone number: ")    amount = float(input("Enter payment amount: "))    initiate_payment(phone_number, amount)